/// Annotations used for generated classes to give them metadata
/// Important metadata includes
/// 1- Friendly name
/// 2- Identifies if the class is generated by Nowa or not

class NowaVar {
  final String? name;
  final String? loader;

  const NowaVar({this.name, this.loader});
}

class NowaFunc {
  final String? name;
  final String? loader;

  const NowaFunc({this.name, this.loader});
}

class NowaClass {
  final String? name;
  final String? loader;

  const NowaClass({this.name, this.loader});
}
